Script.script:
-- Functions to build the bottom UI
-- The layout is:
-- - A main container at 100% of the needed space.
-- - A main label for applying stylesheets to the container. 
--   - This should be slightly smaller than the main container (5px).
-- - MiniConsoles inside the main label as needed.
--   - These should be slightly smaller than the main label (5px).

ui = ui or {}

function BuildBottom()

  -- Main Bottom Container
  -- The "ui" dimension variables are defined in the "2 Init UI" script
  ui.BottomContainer = ui.BottomContainer or Adjustable.Container:new({
    name = "ui.BottomContainer",
    titleText = "Bottom Container",
    x = (ui.LeftContainerWidth + 5), 
    y = "100%-80",
    width = (ui.BottomContainerWidth - 10),
    height = ui.BottomContainerHeight,
    attached = "bottom",
    autoSave = false,
    autoLoad = false,
    adjLabelstyle = ui.MainAdjLabelStyle,
    buttonstyle = ui.MainButtonStyle,
  })
  
  ui.BottomContainer:changeMenuStyle("dark")
  ui.BottomContainer:lockContainer(ui.LockStyle)
  
  -- Main Bottom Label
  ui.BottomContainerLabel = Geyser.Label:new({
    name = "ui.BottomContainerLabel",
    x = 0, y = 0,
    width = "100%",
    height = "100%",
  }, ui.BottomContainer)  -- Attach to the main bottom container
  
  -- Main Bottom Label Stylesheet
  ui.BottomContainerLabel:setStyleSheet(ui.MainStyleSheet)
  
  
  ui.StatusMC = Geyser.MiniConsole:new({
    name = "ui.StatusMC",
    x = 5,
    y = 12,
    width = "100%-10",
    height = "100%-20",
    autoWrap = false,
    scrollBar = false,
    color = ui.BgColor,
    fontSize = 11,
  }, ui.BottomContainerLabel)

  ui.StatusMC:disableScrolling()
  
  -- Call other functions
  SetCMDStyle()
  SetBarStyle()

end


function SetCMDStyle () --Move Command Line

  setCmdLineStyleSheet("main", [[
    QPlainTextEdit {
      padding-left: ]] .. ui.CmdLineStart .. [[px;       /* change 100 to your number */
      background-color: black;   /* change it to your background color */
    }
  ]])

end

function SetBarStyle () --Sets bar settings
  -- Health
  hpbar = Geyser.Gauge:new({
    name="hpbar",
    x = 5,
    y = 0,
    width=120, height=15,
  }, ui.StatusMC)
  hpbar.front:setStyleSheet([[
    background-color: #cc0000;
    border-radius: 2;
  ]])
  hpbar.back:setStyleSheet([[
    background-color: #660000;
    border-radius: 2;
  ]])
  hpbar.text:setStyleSheet([[
    text-align: right;
    padding-right: 5px;
  ]])
  hpbar:setAlignment("center")

  -- Energy
  enbar = Geyser.Gauge:new({
    name="enbar",
    x = 5,
    y = 18,
    width=120, height=15,
  }, ui.StatusMC)
  enbar.front:setStyleSheet([[
    background-color: #0a5293;
    border-radius: 2;
  ]])
  enbar.back:setStyleSheet([[
    background-color: #073763;
    border-radius: 2;
  ]])
  enbar.text:setStyleSheet([[
    text-align: right;
    padding-right: 5px;
  ]])
  enbar:setAlignment("center")
  
  -- Pet Health
  phpbar = Geyser.Gauge:new({
    name="phpbar",
    x = 5,
    y = 36,
    width=120, height=15,
  }, ui.StatusMC)
  phpbar.front:setStyleSheet([[
    background-color: #006600;
    border-radius: 2;
  ]])
  phpbar.back:setStyleSheet([[
    background-color: #660000;
    border-radius: 2;
  ]])
  phpbar.text:setStyleSheet([[
    text-align: right;
    padding-right: 5px;
  ]])
  phpbar:setAlignment("center")
end

-- Erion Green Theme

ui = ui or {}

function ThemeErionGreen()
  ui.BorderColor = "#9f8e3d"  -- Erion Gold
  ui.BgColor = "#0c1510"      -- Erion Green
  ui.BgColorRGB = {12,21,16}  -- Erion Green in RGB (unpack(table))
  ui.LockStyle = "full"  -- Hides all borders. The container must be unlocked with a command.
  ui.MainStyleSheet = [[
      background: ]] .. ui.BgColor .. [[ ;
      border: 3px solid ]] .. ui.BorderColor .. [[ ;
      border-radius: 10px;
    ]]
end

-- Erion Minimal Black Theme

ui = ui or {}

function ThemeMinimalBlack()
  ui.BorderColor = "black"
  ui.BgColor = "black"
  ui.BgColorRGB = {0,0,0}
  ui.LockStyle = "full"  -- Hides all borders. The container must be unlocked with a command.
  ui.MainStyleSheet = [[
      background: ]] .. ui.BgColor .. [[ ;
      border: 0px solid ]] .. ui.BorderColor .. [[ ;
      border-radius: 10px;
    ]]
end

-- Does a full UI refresh

function UIRefresh (event)
  InitUI()
  echo("UI Refreshed!\n")
end

registerAnonymousEventHandler("UI_Refresh", "UIRefresh")

Trigger.script:
-- Trigger: Changing class target -- Pattern (regex): ^You are now targeting the (?<class>\w+) class -- Pattern (regex): ^You are now targeting the (?<class>\w+) subclass -- Pattern (regex): ^Congratulations\! You have added the (?<class>.*) subclass\! -- Description: Update the active class when "class target" is used
ui = ui or {}
ui.ActiveClass = matches.class GetClasses() UpdateStatus()

-- Trigger: Map Trigger (Border) -- Pattern (regex): (^\+-{9}\+\s{2})
ui = ui or {}
if CMap == 0 then ui.AnsiMapMC:clear() selectCaptureGroup(1) setBgColor(unpack(ui.BgColorRGB)) copy() ui.AnsiMapMC:appendBuffer() replace()
CMap = 1 else selectCaptureGroup(1) setBgColor(unpack(ui.BgColorRGB)) copy() ui.AnsiMapMC:appendBuffer() replace() CMap = 2 end
if CMap == 2 then CMap = 0 end

-- Trigger: Map Trigger (Map) -- Pattern (regex): (^\|.{9}\|\s{2})
ui = ui or {}
selectCaptureGroup(1) setBgColor(unpack(ui.BgColorRGB)) copy() ui.AnsiMapMC:appendBuffer() replace("")

-- Trigger: 2 Expedition targets capture -- Pattern (regex): ^Find and (kill|spar with) (?<target>.*) in (?<area>.*)\. -- Description: Capture all lines with the given prefix and write them to a table with color.
ui = ui or {}
if ui.capturingExpedTargets then local t = ("<green>"..matches.target.." <white>in <yellow>"..matches.area) table.insert(ui.eTargets, t) end

-- Trigger: 3 Expedition bonus timer -- Pattern (regex): ^Finish the expedition in under (?<min>\d+) minutes and (?<sec>\d+) seconds for a bonus reward -- Pattern (regex): ^Finish the expedition in under (?<min>\d+) minutes for a bonus reward -- Description: Create an expedition bonus timer.
ui = ui or {}
ui.expedBonusTimerMin = matches.min ui.expedBonusTimerSec = matches.sec or 0 ui.expedBonusTimerTotal = ((matches.min * 60) + matches.sec) ui.expedBonusTimer = tempTimer(ui.expedBonusTimerTotal, function() ui.expedBonusTimer = "0" end)

-- Trigger: Expedition progress -- Pattern (regex): ^You have completed a part of your EXPEDITION -- Pattern (regex): ^You have almost (?<comp>\w+) your EXPEDITION -- Description: Updates the top console that all targets have been eliminated.
ui = ui or {}
if matches.comp then ui.TopContainerMC:clear() ui.TopContainerMC:cecho("<orange_red>EXPEDITION!---------------------------------------------------------------------------\n") ui.TopContainerMC:cecho("<green>Complete! Return to the Expedition Giver!\n") else send("expedition info") end

-- Trigger: 1 Junkyard objects start -- Pattern (regex): ^You ask The Giver for a junkyard quest -- Pattern (regex): ^You have (\d+) minutes left to gather\: -- Pattern (regex): ^You have (\d+) minutes and (\d+) seconds left to gather\: -- Pattern (regex): ^You have (\d+) hour (\d+) minutes and (\d+) seconds left to gather\:
ui = ui or {}
ui.junkyardObjects = {} ui.capturingJunkyardObjects = true ui.onJunkyard = true enableTrigger("2 Junkyard object capture")
-- Use a timer to disable the capture since we do not have an ending string. tempTimer(2, function() disableTrigger("2 Junkyard object capture") ui.capturingJunkyardObjects = false
-- Display the objects in the top console ui.TopContainerMC:clear() ui.TopContainerMC:cecho("<orange_red>JUNKYARD!---------------------------------------------------------------------------\n")
for i = 1, #ui.junkyardObjects, 1 do -- local first = ui.junkyardObjects[i] or "" -- local second = ui.junkyardObjects[i + 1] or "" -- ui.TopContainerMC:cecho(string.format("%-65s %s\n", first, second)) -- string.format adds spacing. ui.TopContainerMC:cecho(ui.junkyardObjects[i].."\n") end end)

-- Trigger: Junkyard end -- Pattern (regex): ^You inform The Giver you have finished eliminating hazardous remnants from the world -- Pattern (regex): ^You have failed your junkyard quest
ui = ui or {}
ui.TopContainerMC:clear() ui.onJunkyard = false

-- TODO: Capture moon events ui = ui or {}
if matches.moon == "red" then ui.RedMoonRising = true end if matches.moon == "white" then ui.WhiteMoonRising = true end
setBgColor(0,25,0)
