for i = 1, #ui.junkyardObjects, 1 do -- local first = ui.junkyardObjects[i] or "" -- local second = ui.junkyardObjects[i + 1] or "" -- ui.TopContainerMC:cecho(string.format("%-65s %s\n", first, second)) -- string.format adds spacing. ui.TopContainerMC:cecho(ui.junkyardObjects[i].."\n") end end)

ui = ui or {}

